import React, { Component, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import url from './../../../config'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams
  } from 'react-router-dom';

    
   const LocationHotel=  (props)=> {
        const {location}= useParams();
        const [data, setData] = useState([]);

        useEffect( async ()=>{
           const data = await axios.get(url + '/rooms?query=searchHotel&location=' + location).then(res=> res.data);
           setData(data);
        },[])
        

        return (
            <div  className="container">
             
              
                <div>               
                    {data.map(item=>{
                            return <div className="row">
                                <div className="col-sm-4">                                                               
                                <img src={item.image} style={{width:'200px'}} className="img-thumbnail "/>
                                </div>
                                <div className="mb-4 col-sm-8 ">                                                   
                                    <div className="card-body">
                                        <h4 className="card-title"> {location} : <span className="text-primary font-weight-bolder">{item.hotel_name}</span> </h4>
                                        <h4 className="card-text text-info "> Type Rooms : <span> {item.roomtype} </span> </h4>
                                        <h4 className="card-text text-info "> Price : <span> {item.rent} $</span> </h4>
                                        <a className="text-link"><i class="fa fa-map-marker" aria-hidden="true"></i> {item.address} </a>
                                        <p className="card-text">{item.des} </p>
                                    </div>
                                </div> 
                                </div>        
                    })}   
                          
                </div>
            </div>
        );
    }


export default LocationHotel;
