import React, { Component } from 'react'
import Item from '../../Item/item_room';
import './room.scoped.css';
import 'bootstrap/dist/css/bootstrap.css';
import Header from '../../header/header';
import axios from 'axios';
import { BrowserRouter as Router, } from "react-router-dom";
import { Route, Link, Switch } from "react-router-dom";
import Detail from '../detail/Detail';
import url from '../../../config'

class Room extends Component {
  constructor(props) {
    super(props);
    this.state = { data: [] }
  }

  componentDidMount() {
    axios.get(url+'/rooms').then(res => {
      this.setState({ data: res.data })
    })
  }
  render() {
    return (
      <div class="container-fluid">
        <Header></Header>
        <section className="body-room-4">
          <div className="container">
            <div className="wrap-room-4">
              <div className="row">
                {this.state.data.map((element, index) => {
                  return (<p>
                    <div class="row">
                      <div class="col-sm-12">
                        <h5>{element.noroom}</h5>
                        <h3>{element.room_id}</h3>
                        <p> <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4zDV7hvBIRIcvWXkkKNfRUsAwo79NxK8aGg&usqp=CAU" /></p>
                        <button class="btn" onload="Refresh()" > <Link to={"/detail/"+element.room_id} >  <Route path={`/detail/${element.room_id}`} component={Detail} /> View detail </Link></button>
                       

                        <h3> -----------------------------</h3>
                      </div>
                    </div>
                  </p>)
                })}

              </div>
            </div>
          </div>
        </section>
      </div>
    )
  }
}
export default Room;
