import React, { Component } from 'react';
import './Detail.css'
import axios from 'axios';
import Rooms from '../room/room'
import reactDom from 'react-dom';
import { data } from 'jquery';
import queryString from 'query-string';
class Detail extends Component {
    constructor(props) {
        super(props);
        this.state = { data: [] }
    }

    componentDidMount() {
        const id = this.props.location.pathname.split('/').pop()
        console.log(id);
        axios.get(`http://localhost/quynh/ReactJS_Hotel/src/DB/api/controller.php/rooms/${id}`).then(res => {
            this.setState({ data: res.data })
        })
    }
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         user: {},
    //         cate: {}
    //     }
    // }
    // componentDidMount() {
    //     const id = this.props.location.pathname.split('/').pop()
    //     console.log(id);

    //     axios({
    //         method: 'GET',
    //         url: `http://localhost/quynh/ReactJS_Hotel/src/DB/api/controller.php/rooms/` + id[1],
    //         data: null
    //     }).then(res => {
    //         this.setState({ user: res.data }, () => axios({
    //             method: 'GET',
    //             url: 'http://localhost/quynh/ReactJS_Hotel/src/DB/api/controller.php/rooms/' + this.state.user['room_id'],
    //             data: null
    //         }).then(res => {
    //             this.setState({ cate: res.data }, () => console.log(this.state.cate));
    //         }));
    //     });
    // }

    render() {
        const id = this.props.location.pathname.split('/').pop()
        console.log(id);


        return (


            <div>
                <div class="container">
                    <center> <h3 class="headerDetail"> Welcome to view detail room </h3></center>
                    <div></div>
                    <div class="row">
                        <div class="col-sm-5" > <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThWogExiuuBvNYxY7rq2fQQ5CicmrYz420ZQ&usqp=CAU" alt="Girl in a jacket" height="100%" width="100%" ></img>
                        </div>
                        <div class="col-sm-7" >
                            <div class="spa">
                                <div class="space">
                                    <div class="spa">
                                        <h6 class="sao"> *********** </h6>
                                    </div>
                                    <div>
                                        <strong> <h4 class="moderndb"> MODERN DELEX BELVEDERE  </h4></strong>
                                    </div>

                                </div>
                                <div class="spac">
                                    <div class="space1">
                                        <h4 class="gachdung"> | </h4>
                                    </div>
                                    <div>
                                        <h5 class="tien"> $ 75000 </h5>
                                        <small> per night</small>
                                    </div>
                                </div>
                            </div>
                            <div class="dtail">
                                <h4 class="gach">____</h4>
                                <div class="row">

                                  
                                    <div>
                                        <div class="col-sm-2">
                                            <h5> Room_id</h5>
                                            <h5> {id} </h5>
                                        </div>
                                        <div class="col-sm-2">
                                            <h5> Roomtype_id</h5>
                                            <h5> {id.roomtype_id} </h5>
                                        </div>
                                        <div class="col-sm-3">
                                            <h5> Status</h5>
                                            <h5> {id.status} </h5>
                                        </div>
                                        <div class="col-sm-2">
                                            <h5> noroom</h5>
                                            <h5> {id.noroom} </h5>
                                        </div>
                                        <div class="col-sm-3">
                                            <h5> Phone</h5>
                                            <h5> {this.state.phone} </h5>
                                        </div>

                                    </div>
                                    {/* <div className="col-lg-6">
                                        <ul className="list-inline mb-2">
                                            {(() => {
                                                for (let i = 0; i <= this.state.user['room_id']; i++) {
                                                    return <li className="list-inline-item m-0"><i className="fas fa-star small text-warning" /></li>
                                                }
                                            })()}
                                        </ul>
                                        <h1>{[this.state.user['room_id']]}</h1>
                                        {(() => {
                                            if (this.state.user['room_id'] != 0) {
                                                return <Detail><strike><p className="text-muted lead">$ {[this.state.user['status']]}</p></strike><p className="text-muted lead">$ {[this.state.user['noroom']]}</p></Detail>
                                            }
                                            else {
                                                return <p className="text-muted lead">$ {[this.state.user['phone']]}</p>
                                            }
                                        })()}
                                    </div> */}

                                </div>

                            </div>

                        </div>
                    </div>
                </div>
            </div>

        );


    }

}


export default Detail;
