import React, { Component, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import url from './../../../config'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams
  } from 'react-router-dom';

    
   const LocationHotel=  (props)=> {
        const {location}= useParams();
        const [data, setData] = useState([]);

        useEffect( async ()=>{
           const data = await axios.get(url + '/rooms?query=searchHotel&location=' + location).then(res=> res.data);
           setData(data);
        },[])
        

        return (
            <div  className="container">
                <h1 className="mb-4"> {location} </h1>
               <div className="col-sm-2"> 
                </div>      
              
                <div className=" card card-deck col-sm-9"> 
              
                    {data.map(item=>{
                            return <div>      
                            <img src="/images/home/danang.jpg" style={{width:'250px'}} className="img-thumbnail rounded float-left"/>
                                <div className="card mb-4 float-right ">                                                   
                                    <div className="card-body">
                                        <h4 className="card-title"> {location} : <span className="text-primary font-weight-bolder">{item.hotel_name}</span> </h4>
                                        <h4 className="card-text text-info "> Price : <span> {item.rent}</span> </h4>
                                        <a className="text-link"><i class="fa fa-map-marker" aria-hidden="true"></i> 1118 Quang Trung, Phường 8, Gò Vấp, Thành phố Hồ Chí Minh, Vietnam </a>
                                        <p className="card-text"> Situated on the unspoilt shores of Hoi An Beach, Four Seasons The Nam Hai, Hoi An, Vietnam offers luxurious villas with iPod docks and free WiFi access. It boasts 3 beachfront pools and a spa. </p>
                                    </div>
                                </div> 
                                </div>        
                    })}   
                          
                </div>
            </div>
        );
    }


export default LocationHotel;
