import React, { Component, createElement } from 'react';
import Item_Home from './../../Item/home_item'
import './style.scoped.css'
import 'bootstrap/dist/css/bootstrap.css';
import Footer from './../../footer/footer'
import Header from './../../header/header';
import url from './../../../config'
import axios from 'axios'
import Carousel from 'react-bootstrap/Carousel';
import { fireEvent } from '@testing-library/dom';
import ReactDOM from 'react-dom';

const SliderHeader = React.lazy(() => import('./../../header/sliderHeader'));


class Home extends Component {

  constructor(props) {
    super(props);
    this.state = { dataRoom: [], display: [] , arr:[]};
  }

  searchRoom = async () => {
    return await axios.get(url + "/rooms?query=searchRoom")
  }
  getRoomTypeById = async (id) => {
      await axios.get(url + "/roomtypes?roomtype_id=" + id).then(res =>{ let x = this.state.arr; x.push(res.data);  this.setState({arr:x});console.log(this.state) });
      
  }
  getRoomType  = (array) => {
       array.filter((item, index) => array.indexOf(item) === index).forEach( id =>  this.getRoomTypeById(id));

  }
  componentDidMount = async () => {
  
    
    await this.searchRoom().then(res => {this.setState({dataRoom:res.data}); console.log(res.data) });   


    // for (let i = 0; i < data.length; i++) {

    //   for (let j = 0; j < this.state.arr.length; j++) {
    //     const element = this.state.arr[j];
    //     if (element.roomtype_id == data[i].roomtype_id) {
    //       data[i].rent = element.rent;
    //       data[i].quantity = element.roomtype;
    //       data[i].roomtype = element.roomtype;
    //     }
    //   }
    // };



    // this.setState({ dataRoom: data }, () => {
    //   let data = this.state.dataRoom;
    //   let data2 = [];

    //   for (let i = 0; i < data.length / 4; i++) {
    //     let a;
    //     let b;
    //     let c;
    //     let d;
    //     //let value= data.shift();
    //     a = React.createElement(Item_Home, { image: "images/home/Presidential1.jpg", roomtype: "Luxury" });

    //     //value= data.shift();
    //     b = React.createElement(Item_Home, { image: "images/home/Presidential1.jpg", roomtype: "Luxury" });
    //     // value= data.shift();
    //     c = React.createElement(Item_Home, { image: "images/home/Presidential1.jpg", roomtype: "Luxury" });
    //     // value= data.shift();
    //     d = React.createElement(Item_Home, { image: "images/home/Presidential1.jpg", roomtype: "Luxury" });
    //     let div2 = React.createElement('div', { className: "row" }, a, b, c, d);
    //     let element = React.createElement(Carousel.Item, {}, div2);
    //     data2.push(element);

    //   }
    //   let x = this.state.display;
    //   x.push(data2);
    //   this.setState({ display:  data2.push(element)      }, () => {
    //     console.log(this.state.display[0].map(value=> value))

    //     console.log(typeof this.state.display)
    //   })
    // })


  }
  *chunks(arr, n) {
    for (let i = 0; i < arr.length; i += n) {
      yield arr.slice(i, i + n);
    }
  }
  componentDidUpdate = (prevProps, prevState) => {



    // return <Item_Home image="images/home/Presidential1.jpg" roomtype={value.rate} des=""></Item_Home>;

    // <Carousel.Item>
    //   <div className="row ">
    //     <Item_Home image="images/home/Presidential1.jpg" roomtype="Luxury" des=""></Item_Home>
    //   </div>
    //   <br /> <hr /> <br />


    // </Carousel.Item>
  }
  render() {
    const {dataRoom}=this.state;
    this.chunks(dataRoom, 4)
    return (
      <div>
        <Header />
        <SliderHeader></SliderHeader>
        <div className="container">
          <center>
            <h1 style={{ fontFamily: 'Didot', color: '#CC6600' }}>WELCOME TO HKTQUEEN HOTEL</h1>
          </center><br />
          <div className="row infor_1 ">
            <div className="col-md-5 " style={{ fontSize: '22px', fontFamily: 'Optima', color: '#003300' }}>HKT Queen Hotel Đà Nẵng City is a world-class 5-star hotel, located at the intersection of four main districts, which is considered the heart and center of the city Đà Nẵng. It takes you only 35 minutes drive from the hotel to Đà Nẵng
