import React, { useEffect, useState } from 'react';

import './assets/css/animate.scoped.css'
import './assets/css/sidebar-menu.scoped.css'
import './assets/css/adminstyle.scoped.css'
import axios from 'axios';
import url from '../config'
import MenuBar from './menubar'
import Header from './header';
import Carousel from 'react-bootstrap/Carousel';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams,
    Link, useHistory
} from 'react-router-dom';


const makeTextAction = (str) => {

    if (str == "success" || str == "expired")
        return "no action";
    if (str == "accept")
        return "cancel";
    if (str == "received")
        return "accept";
}

const makeBtnColor = (str) => {

    if (str == "success")
        return <button className=" btn btn-success" name="updateRequest">{str} </button>
    if (str == "expired")
        return <button className=" btn btn-warning" name="updateRequest"> {str} </button>

    if (str == "accept")
        return <button className=" btn btn-info" name="updateRequest"> {str}</button>
    if (str == "cancel")
        return <button className=" btn btn-danger" name="updateRequest"> {str}</button>
    if (str == "received")
        return <button className=" btn btn-primary" name="updateRequest">{str} </button>

}
const makeTextColor = (str) => {

    if (str == "success")
        return < span className=" btn btn-success" >{str} </span>
    if (str == "expired")
        return <span className=" btn btn-warning" > {str} </span>
    if (str == "accept")
        return <span className=" btn btn-info" > {str}</span>
    if (str == "cancel")
        return <span className=" btn btn-danger" > {str}</span>
    if (str == "received")
        return <span className=" btn btn-primary" >{str} </span>

}
const View = (props) => {
    let history = useHistory()
    const { id } = useParams();
    const [data, setData] = useState([]);

    useEffect(async () => {
        const data = await axios.get(url + '/providers?query=view&id_user=' + JSON.parse(localStorage.getItem('id_user'))).then(res => res.data);
        setData(data);
    }, [])

    return (
        <div>

            <div className="bg-theme bg-theme1">
                <div id="wrapper">
                    <MenuBar></MenuBar>
                    <Header></Header>
                    <div className="clearfix" />
                    <div className="content-wrapper">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-12 col-lg-12">
                                    <div className="card">
                                        <h2>View Booking</h2>
                                        <div className="table-responsive">
                                            <table className="table align-items-center table-flush table-borderless table-hover">
                                                <thead>
                                                    <tr>
                                                        <th>STT</th>
                                                        <th>Room_id</th>
                                                        <th>Status</th>
                                                        <th>Hotel_name</th>
                                                        <th>Checkin</th>
                                                        <th>Checkout</th>
                                                        <th>Roomtype</th>
                                                        <th>Rent</th>
                                                        <th> Action</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {data.map((element, index) => {
                                                        return (<tr>
                                                            <td>{index}</td>
                                                            <td>{element.room_id}</td>
                                                            <td>{makeTextColor(element.status)}</td>
                                                            <td>{element.hotel_name}</td>
                                                            <td>{element.checkin}</td>
                                                            <td>{element.checkout}</td>
                                                            <td>{element.roomtype}</td>
                                                            <td>{element.rent}</td>
                                                            <td>
                                                                {makeBtnColor(makeTextAction(element.status))}
                                                            </td>
                                                        </tr>)
                                                    })}

                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </div>

        </div>

    );
}


export default View;
