import './basket.scoped.css'
import 'bootstrap/dist/css/bootstrap.css';
import Footer from './../footer/footer'
import Header from './../header/header'
import url from '../../config'
import axios from 'axios'

import React, { Component, useEffect, useState } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams,
    useLocation,
    Link
  } from 'react-router-dom';
function useQuery() {
    return new URLSearchParams(useLocation().search);
  }
 const Basket=()=>{
    const {local}=useParams();
    const query= useQuery();
    const [data, setData] = useState([]);
    const [room, setRoom] = useState([]);

    useEffect( async ()=>{
      let aa   =  await axios.get(url +'/baskets?user_id='+JSON.parse(localStorage.getItem('id_user'))) .then(res=> res.data);
      console.log(aa);
        setData(aa);
      },[]);
    //   useEffect( async ()=>{
    //     let aa   =  await axios.get(url +'/baskets?user_id='+JSON.parse(localStorage.getItem('id_user'))) .then(res=> res.data);
    //     console.log(aa);
    //       setData(aa);
    //     },[]);
        return (
            <div>
                <Header></Header> 
               
            <div className="container px-4 py-5 mx-auto">  

                <div className="row d-flex justify-content-center">

                    <div className="col-3">
                        <h4 className="heading">Booking rooms</h4>
                    </div>
                    <div className="col-7">
                        <div className="row text-right">
                         
                            <div className="col-2">
                                <h6 className="mt-2">Room</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mt-2">Price</h6>
                            </div>

                            <div className="col-2">
                                <h6 className="mt-2">Checkin</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mt-2">Checkout</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mt-2">Hotel</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mt-2">Status</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mt-2">Key </h6>
                            </div>
                            
                        </div>
                    </div>
                </div>

              
              {data.map(value=>{
                  return (
                    <div className="row d-flex justify-content-center border-top">
                    <div className="col-3">
                        <div className="row d-flex">
                            <div className="book"> <img src={value.image} className="book-img" /> </div>
                            
                        </div>
                    </div>
                    <div className="my-auto col-7">
                        <div className="row text-right">
                            <div className="col-2">
                                <p className="mob-text">{value.norom}</p>
                            </div>

                            <div className="col-2">
                                <h6 className="mob-text">{value.rent}</h6>
                            </div>
                            <div className="col-2">
                                <h6 className="mob-text">$9.99</h6>
                            </div>

                            <div className="col-2">
                                <p className="mob-text">Còn phòng</p>
                            </div>

                            <div className="col-2">
                                <p className="mob-text">Hà Nội</p>
                            </div>

                            <div className="col-2">
                                <p className="mob-text"></p>
                            </div>

                        </div>
                    </div>
                </div> )
              })}
              
                <div className="container bg-light rounded-bottom py-4" id="zero-pad">
                    <div className="row d-flex justify-content-center">
                    <div className="col-lg-10 col-12">
                        <div className="d-flex justify-content-between align-items-center">
                        <div className="px-md-0 px-1" id="footer-font"> <b className="pl-md-4">SUBTOTAL<span className="pl-md-4">$61.78</span></b> </div>
                        <div> <button className="btn btn-sm bg-dark text-white px-lg-5 px-3">CONTINUE</button> </div>
                        </div>
                    </div>
                    </div>
                </div>

            </div>
            <Footer></Footer>
            </div>

        );
    }



export default Basket;
