import React, { useEffect, useState } from 'react';
import './detail.scoped.css'
import axios from 'axios';
import url from './../../../config'
import Rooms from '../room/room'
import reactDom from 'react-dom';
import { data } from 'jquery';
import queryString from 'query-string';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams
} from 'react-router-dom';


const Detail = (props) => {
    const { id } = useParams();
    const [data, setData] = useState([]);

    useEffect(async () => {
        const data = await axios.get(url + '/rooms?room_id=' + id).then(res => res.data);
        console.log(data)

        setData(data);
    }, [])

    return (
        <div>
            <div class="container">
                <center> <h3 class="headerDetail"> Welcome to view detail room </h3></center>
                <div></div>
                <div class="row">
                    <div class="col-sm-5" > <img src={data.img} alt="Image rooms" height="100%" wdatath="100%" ></img>
                    </div>
                    <div class="col-sm-7" >
                        <div class="spa">
                            <div class="space">
                                <div class="spa">
                                    <h6 class="sao"> *********** </h6>
                                </div>
                                <div>
                                    <strong> <h4 class="moderndb"> MODERN DELEX BELVEDERE  </h4></strong>
                                </div>

                            </div>
                            <div class="spac">
                                <div class="space1">
                                    <h4 class="gachdung"> | </h4>
                                </div>
                                <div>
                                    <h5 class="tien"> {data.rent} $</h5>
                                    <small> per night</small>
                                </div>
                            </div>
                        </div>
                        <div class="dtail">
                            <h4 class="gach">____</h4>
                            <div class="row">

                                <div>
                                    <div className="mb-4 col-sm-9 card-body ">
                                        <div className="card">
                                            <h4 className="card-title"> {data.location} : <span className="text-primary ">{data.hotel_name}</span> </h4>
                                            <h4 className="card-text text-info "> Type Rooms : <span> {data.roomtype} </span> </h4>
                                            <h4 className="card-text text-info "> Price : <span> {data.rent} $</span> </h4>
                                            <a className="text-link"><i class="fa fa-map-marker" aria-hidden="true"></i> {data.address} </a>
                                            <p className="card-text">{data.des} </p>
                                        </div>
                                    </div>
                                    <div class="col-sm-3">
                                        <h5> Roomtype: {data.roomtype}</h5>
                                    </div>
                                    <div class="col-sm-3">
                                        <h5> Status:  {data.status}</h5>
                                    </div>
                                    <div class="col-sm-3">
                                        <h5> noroom: {data.noroom}</h5>
                                    </div>
                                    <div class="col-sm-3">
                                        <h5> Phone:{data.phone}</h5>
                                    </div>

                                </div>
                                {/* <div className="col-lg-6">
                                        <ul className="list-inline mb-2">
                                            {(() => {
                                                for (let i = 0; i <= this.state.user['room_id']; i++) {
                                                    return <li className="list-inline-data m-0"><i className="fas fa-star small text-warning" /></li>
                                                }
                                            })()}
                                        </ul>
                                        <h1>{[this.state.user['room_id']]}</h1>
                                        {(() => {
                                            if (this.state.user['room_id'] != 0) {
                                                return <Detail><strike><p className="text-muted lead">$ {[this.state.user['status']]}</p></strike><p className="text-muted lead">$ {[this.state.user['noroom']]}</p></Detail>
                                            }
                                            else {
                                                return <p className="text-muted lead">$ {[this.state.user['phone']]}</p>
                                            }
                                        })()}
                                    </div> */}

                            </div>

                        </div>

                    </div>
                </div>
            </div>
        </div>

    );


}



export default Detail;
