import React, { Component } from 'react';
import './style.scoped.css'
import { Link } from 'react-router-dom';
import axios from 'axios';

class location_item extends Component {
    constructor(props) {
        super(props);
        this.state = { dataRoom: [], display: [] , arr:[]};
      }
      componentDidMount = async () => {
  
    
        await this.searchRoom().then(res => {this.setState({dataRoom:res.data}); console.log(res.data) });   
      }
    render() {
        return (
            
            <div className={this.props.className}>
            <Link>
              <div className="thumbnail">
                <img className="card-img-top image-big " src={this.props.image} alt="HKTQueen" />
              </div>
              </Link >
            </div>
           
        );
    }
}

export default location_item;
